import React from 'react';
import { db } from './firebase';
import "./Orders.css";

function Orders() {

  const [orders, setOrders] = useState([]);
  useEffect(() => {
    if (user) {
      db.collection("users").doc(user?.uid).collection("orders").orderBy("created", "des").onSnapshot((snapshot) => setOrders(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data(),
        }))
      )
    );
  }else{
    setOrders([])
  }
},[user])


  return (
    <div className="orders">
      <h1>Orders pages</h1>

    </div>
  )
}

export default Orders
